import React from 'react';
import MapDiv from './MapDiv.js';
import '../App.css';
import RestaurantList from './RestaurantList.js';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

class RestaurantBox extends React.Component {
  constructor(props) {
      super(props);
      this.state = {
          Latitude:1,
          Longitude:1,
          restaurants: [],
      }
  }
  componentDidMount() {
    const success = (pos) => {
      var crd = pos.coords;
      this.setState({
        Latitude: crd.latitude,
        Longitude: crd.longitude
      },()=> {
        fetch('https://cors-anywhere.herokuapp.com/'+'https://maps.googleapis.com/maps/api/place/nearbysearch/json?location='+this.state.Latitude +','+ this.state.Longitude + '&radius=150000&type=restaurant&key=AIzaSyAR14v1v6okXPc5QrZwvsmMbaHktnQ0M5I')
       .then(response=>response.json()).then(data =>{
         console.log(data);
         this.setState({
           restaurants: data.results
         });

       })

      })

    };

    const error = (err) => {
      console.warn(`ERROR(${err.code}): ${err.message}`);
    };

    navigator.geolocation.getCurrentPosition(success, error);


}


  render() {
    return (

      <div className="map-box row align-items-center" >
        <Container>
          <Row>
            <Col>
              {this.state.restaurants !== []?
                <MapDiv  restaurants={this.state.restaurants}  Latitude={this.state.Latitude} Longitude={this.state.Longitude} /> : null}
           </Col>
          <Col>
             {this.state.Latitude !== 1?  <RestaurantList restaurants={this.state.restaurants}   Latitude={this.state.Latitude} Longitude={this.state.Longitude} />: null}
          </Col>
          </Row>
      </Container>

    </div>

    );
  }
}

export default RestaurantBox;
